import styled from "styled-components";

export const Title = styled.h1`
  text-align: center;
  text-transform: capitalize;
  color: var(--brand-color);
  margin: 40px 0 10px;
`;
export const Small = styled.small`
display:block;
  text-align: center;
  text-transform: capitalize;
  color: var(--brand-color);
  font-size:14px;
  font-weight:600;

`;
export const Letter = styled.p`
display:block;
  text-align: center;

  color: var(--brand-color);
  font-size:16px;
  font-weight:400;
  width:800px;
  margin:0 auto;

`;
