import React,{useState, useEffect} from 'react';
import {Form, Head} from './styles'

const Canciones = () => {

  const [letras, setLetras] = useState({})
  const [formArtist, setFormArtist] = useState("");
  const [formSong, setFormSong] = useState("");
  const API = `https://api.lyrics.ovh/v1/${formArtist}/${formSong}`;

    useEffect(() => {
      traerLetra()
    }, []);

    const traerLetra=()=>{
    window
      .fetch(`${API}`)
      .then((response) => response.json())
      .then((data) => {
        setLetras(data);
        console.log(data);
      });
    }

  const handlesubmit=(e)=>{
    e.preventDefault();
    console.log('hola')
    traerLetra()
    return(
      <div>hola</div>
    )
  }

  const handleChange=(e)=>{
    setFormArtist(e.target.value)
  }
  const handleChange2=(e)=>{
    setFormSong(e.target.value)
  }

  return (
    <div>
      <Head>busca con el nombre de tu artista y el nombre de la cancion</Head>
      <Form onSubmit={handlesubmit}>
        <input type='text' placeholder='artista' onChange={handleChange}  />
        <input type='text' placeholder='cancion' onChange={handleChange2} />
        <input type="submit" value="buscar" />
      </Form>
      <div>
        <h2>{formArtist}</h2>
        <p>{formSong}</p>
        <p>{letras.lyrics}</p>
      </div>
    </div>
  )
}

export default Canciones;
