import React,{useState, useEffect} from 'react';

const Canciones = () => {
  const artist='juanes'
    const API = `https://api.lyrics.ovh/v1/${artist}/${cancion}`;
  const [letras, setLetras] = useState([])

    useEffect(() => {
      window
        .fetch(`${API}`)
        .then((response) => response.json())
        .then((data) => {
          setLetras(data);

        });
    }, []);
  return (
    <div>
      letras
    </div>
  )
}

export default Canciones;
