import styled from "styled-components";

export const Title = styled.h1`
  text-align: center;
  text-transform: capitalize;
  color: var(--brand-color);
  margin: 40px 0 0;
`;
export const Small = styled.small`
display:block;
  text-align: center;
  text-transform: capitalize;
  color: var(--brand-color);
  font-size:14px;
  font-weight:600;
  margin: 40px 0;
`;
