import React, {useState, useEffect} from 'react';
import { Item, Title } from "./styles";
import Link from 'next/link'

const List = () => {
  const[listItem, setListItem]=  useState([]);
  const[artist, setArtist]=  useState('artist');
  const API = "http://www.songsterr.com/a/ra/songs/byartists.json?";

  useEffect(()=>{
    window
      .fetch(`${API}artists=muerdo`)
      .then((response) => response.json())
      .then(data=>{
        console.log(data)
        setListItem(data)
        setArtist(data[0].artist.name)
      });
  },[])

  return (
    <div>
    <Title>lo mejor de {artist}</Title>
      {listItem.map((song)=>{
        return (
          <Item key={song.id}>
            <p href="">{song.title}</p>
            <Link href=""> escuchar</Link>
            <Link href=""> letra</Link>
            <Link href=""> acordes</Link>
          </Item>
        );
      })}
    </div>
  );
};

export default List;
